import './App.css';
import Modal from './Modal'
import React, { useState, useEffect } from 'react';
import CarryOn from './components/carryon'
import Checked from './components/checked'
import CarryLimit from './components/carrylimit'

function App() {

  const [modalVisible, setModalVisible] = useState(false)
  const [modalCase, setModalCase] = useState("")

  const openModal = color => {
    setModalVisible(true)
    setModalCase(color)
  }
  const closeModal = () => {
    setModalVisible(false)
  }
  const chooseModal = () => {
    switch(modalCase) {
      case 'red':
        return <CarryOn />
      case 'yellow':
        return <Checked />
      case 'green':
        return <CarryLimit />
      default:
        return;
    }
  }
  const imageChange = (e) => {
    console.log(e.target.files)
    var parent = document.querySelector('.filebox')
    parent.children('.upload-display').remove();

    if(window.FileReader){
      if(!$(this)[0].files[0].type.match(/image\//)) return;

      var reader = new FileReader();
      reader.onload = function(e){
        var src = e.target.result;
        parent.prepand('<div class="upload-display"><div class="upload-thumb-wrap"><img src="'+src+'" class="upload-thumb"></div></div>')
      }
      reader.readAsDataURL($(this)[0].files[0]);
    }

    else{
      $(this)[0].select();
      $(this)[0].blur();
      var imgSrc = document.getSelection.createRange().text;
      parent.prepand('<div class="upload-display"><div class="upload-thumb-wrap"><img class="upload-thumb"></div></div>');

      var img = $(this).siblings('.upload-display').find('img');
      img[0].style.filter = "progid:DXImageTransform.Microsoft.AlphaImageLoader(enable='true',sizingMethod='scale',src=\""+imgSrc+"\")";
    }
  }

  return (
    <div className="App">
      <h1>반입금지 셀프 체크</h1>
      <p><a onClick={() => {openModal('red')}} className="linker">금지 물품</a>은 <span className="red_background">빨간색</span>으로</p>
      <p><a onClick={() => {openModal('green')}} className="linker">기내 또는 위탁 가능 물품</a>은 <span className="green_background">초록색</span>으로</p>
      <p><a onClick={() => {openModal('yellow')}} className="linker">크기 또는 용량 제한 물품</a>은 <span className="yellow_background">노란색</span>으로</p>
      <p>표시됩니다. 사진을 업로드해주세요!</p>

      {
        modalVisible && <Modal
        visible={modalVisible}
        closable={true}
        maskClosable={true}
        onClose={closeModal}>
          { chooseModal() }
        </Modal>
      }

      <div className="card">
        <p><strong>업로드 전 체크해주세요</strong></p>
        <ul className="check">
          <li>무늬가 없는 배경에서 촬영해주세요</li>
          <li>물품 전체가 나오도록 촬영해주세요</li>
        </ul>
      </div>

      <button className="btn">셀프 체크 시작</button>

      <div class="filebox preview-image">
        <input class="upload-name" value="파일선택" disabled="disabled" />
        <label for="input-file">업로드</label>
        <input type="file" id="input-file" class="upload-hidden" onChange={imageChange}/>
      </div>

    </div>
  );
}

export default App;
