import './App.css';
import Modal from './Modal'
import React, { useState } from 'react';
import CarryOn from './components/carryon'
import Checked from './components/checked'
import CarryLimit from './components/carrylimit'
import API from './api-server'

function App() {

  const [modalVisible, setModalVisible] = useState(false)
  const [modalCase, setModalCase] = useState("")
  const [selectedFile, setSelectedFile] = useState({'name':'사진을 업로드해주세요.'})
  const [resultFile, setResultFile] = useState(null)

  const openModal = color => {
    setModalVisible(true)
    setModalCase(color)
  }
  const closeModal = () => {
    setModalVisible(false)
  }
  const chooseModal = () => {
    switch(modalCase) {
      case 'red':
        return <CarryOn />
      case 'yellow':
        return <Checked />
      case 'green':
        return <CarryLimit />
      default:
        return;
    }
  }
  const submitImage = () => {
    const formData = new FormData();
    formData.append('img', selectedFile);
    API.imageSend(formData)
      .then(resp => console.log(resp))
      .catch(error => console.log(error))
  }

  return (
    <div className="App">
      <h1>반입금지 셀프 체크</h1>
      <p><span onClick={() => {openModal('red')}} className="linker">금지 물품</span>은 <span className="red_background">빨간색</span>으로</p>
      <p><span onClick={() => {openModal('green')}} className="linker">기내 또는 위탁 가능 물품</span>은 <span className="green_background">초록색</span>으로</p>
      <p><span onClick={() => {openModal('yellow')}} className="linker">크기 또는 용량 제한 물품</span>은 <span className="yellow_background">노란색</span>으로</p>
      <p>표시됩니다. 사진을 업로드해주세요!</p>

      {
        modalVisible && <Modal
        visible={modalVisible}
        closable={true}
        maskClosable={true}
        onClose={closeModal}>
          { chooseModal() }
        </Modal>
      }

      <div className="card">
        <p><strong>업로드 전 체크해주세요</strong></p>
        <ul className="check">
          <li>무늬가 없는 배경에서 촬영해주세요</li>
          <li>물품 전체가 나오도록 촬영해주세요</li>
        </ul>
      </div>

      <div class="filebox">
        <input class="upload-name" value={selectedFile.name} disabled="disabled" />
        <label htmlFor="input-file" className="input-file-btn">사진 업로드</label>
        <input type="file" id="input-file" class="upload-hidden" style={{display:"none"}} onChange={(e) => setSelectedFile(e.target.files[0])}/>
      </div>
      <button className="btn" onClick={submitImage}>셀프 체크 시작</button>

      { resultFile ? <div>{resultFile}</div> : <div>없음</div>}

    </div>
  );
}

export default App;
